import styled from "styled-components";
import defaultPicture from "../../assets/DSC08941.jpg";

const CardDiv = styled.div`
  display: flex;
  position: relative;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 150px;
  height: 150px;
  background-color: aquamarine;
  border-radius: 100%;
  margin: 0.5rem;
  & img {
    object-fit: cover;
    border-radius: 100%;
    width: 90%;
    height: 90%;
  }
  & span.title {
    position: absolute;
    bottom: 1rem;
    left: 50%;
    transform: translateX(-50%);
    font-size: 1rem;
    font-weight: bold;
    text-align: center;
    color: aquamarine;
    white-space: nowrap;
  }
  & span.job {
    position: absolute;
    top: 0.5rem;
    right: -1rem;
    font-size: 0.8rem;
    padding: 0.2rem 0.5rem;
    background-color: aquamarine;
    border-radius: 1rem;
  }
`;

const Card = ({ label, title, picture = defaultPicture }) => {
  return (
    <CardDiv>
      <span className="job">{label}</span>
      <img src={picture} alt="freelance" />
      <span className="title">{title}</span>
    </CardDiv>
  );
};

export default Card;
