import { useParams, Link } from "react-router-dom";
import styled from "styled-components";

const StyledLink = styled(Link)`
  padding: 0.4rem 1rem;
  margin-left: 0.2rem;
  border: 2px solid aquamarine;
  border-radius: 1rem;
  text-decoration: none;
  color: aquamarine;
  transition: all 0.2s ease-in-out;
  visibility: ${(props) => (props.isVisible ? "visible" : "hidden")};
  &:hover {
    color: white;
    background-color: aquamarine;
  }
`;

const Survey = () => {
  const { questionNumber } = useParams();

  useEffec(() => {
    fetch(`http://localhost:8000/survey`)
         .then((response) => response.json()
         .then(({ surveyData }) => console.log(surveyData))
         .catch((error) => console.log(error))
     )
 }, [])
  return (
    <section>
      <h1>Survey ðŸ§©</h1>
      <div>
        <StyledLink
          to={questionNumber > 1 && `/survey/${Number(questionNumber) - 1}`}
          isVisible={questionNumber > 1 && questionNumber !== "results"}
        >
          Previous
        </StyledLink>
        <StyledLink
          to={
            questionNumber < 10
              ? `/survey/${Number(questionNumber) + 1}`
              : "/survey/results"
          }
          isVisible={questionNumber !== "results"}
        >
          {questionNumber < 10 ? "Next" : "Results"}
        </StyledLink>
      </div>
      <p>{questionNumber <= 10 ? `Question ${questionNumber}` : "Results"}</p>
    </section>
  );
};

export default Survey;
