import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import styled from "styled-components";

const StyledLink = styled(Link)`
  padding: 0.4rem 1rem;
  margin-left: 0.2rem;
  border: 2px solid aquamarine;
  border-radius: 1rem;
  text-decoration: none;
  color: aquamarine;
  transition: all 0.2s ease-in-out;
  visibility: ${(props) => (props.isVisible ? "visible" : "hidden")};
  &:hover {
    color: white;
    background-color: aquamarine;
  }
`;

const Survey = () => {
  const { questionNumber } = useParams();
  const [surveyData, setSurveyData] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:8000/survey`).then((response) =>
      response
        .json()
        .then(({ surveyData }) => setSurveyData(surveyData))
        .catch((error) => console.log(error))
    );
  }, []);

  return (
    <section>
      <h1>Survey ðŸ§©</h1>
      <div>
        <StyledLink
          to={questionNumber > 1 && `/survey/${Number(questionNumber) - 1}`}
          isVisible={questionNumber > 1 && questionNumber !== "results"}
        >
          Previous
        </StyledLink>
        <StyledLink
          to={
            questionNumber < 6
              ? `/survey/${Number(questionNumber) + 1}`
              : "/survey/results"
          }
          isVisible={questionNumber !== "results"}
        >
          {questionNumber < 6 ? "Next" : "Results"}
        </StyledLink>
      </div>
      <div>
        
      </div>
      <h2>{questionNumber <= 6 ? `Question ${questionNumber}` : "Results"}</h2>
      <p>{surveyData[questionNumber]}</p>
    </section>
  );
};

export default Survey;
