import { useContext, useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import styled from "styled-components";

import { Loader } from "../../utils/Atoms";
import { SurveyContext } from "../../utils/context";

const StyledLink = styled(Link)`
  padding: 0.4rem 1rem;
  margin-left: 0.2rem;
  border: 2px solid aquamarine;
  border-radius: 1rem;
  text-decoration: none;
  color: aquamarine;
  transition: all 0.2s ease-in-out;
  visibility: ${(props) => (props.isVisible ? "visible" : "hidden")};
  &:hover {
    color: white;
    background-color: aquamarine;
  }
`;

const QWrapper = styled.div`
  margin-top: 2rem;
  background-color: #7fffd42c;
  border-radius: 1rem;
  padding: 0.5rem 1rem;
  box-shadow: 0 0 2rem 0.1rem #0000002c;
  & h2 {
    margin: 0;
    font-size: 1rem;
    border: 2px solid aquamarine;
    border-radius: 1rem;
    width: fit-content;
    padding: 0.2rem 1rem;
    background-color: aquamarine;
    color: white;
  }
  & p {
    border-left: 2px solid aquamarine;
    padding-left: 1rem;
  }
  & div {
    margin: 1rem;
  }
`;

const ReplyBox = styled.button`
  border: none;
  height: 50px;
  width: 250px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: aquamarine;
  border-radius: 30px;
  cursor: pointer;
  box-shadow: ${(props) =>
    props.isSelected ? `0px 0px 10px 5px white inset` : "none"};
  &:first-child {
    margin-right: 15px;
  }
  &:last-of-type {
    margin-left: 15px;
  }
`;

const ReplyWrapper = styled.div`
  display: flex;
  flex-direction: row;
  
  margin: 50px 0;
`;

const Survey = () => {
  const { questionNumber } = useParams();
  const questionNumberInt = parseInt(questionNumber);
  const nextQuestionNumber = questionNumberInt + 1;
  const prevQuestionNumber =
    questionNumberInt === 1 ? 1 : questionNumberInt - 1;

  const [isDataLoading, setDataLoading] = useState(false);
  const [surveyData, setSurveyData] = useState([]);
  const [error, setError] = useState(false);

  const { answers, saveAnswers } = useContext(SurveyContext);

  useEffect(() => {
    async function fetchSurvey() {
      setDataLoading(true);
      try {
        const res = await fetch(`http://localhost:8000/survey`);
        const { surveyData } = await res.json();
        setSurveyData(surveyData);
      } catch (err) {
        console.log(err);
        setError(true);
        alert(err);
      } finally {
        setDataLoading(false);
      }
    }
    fetchSurvey();
  }, []);

  function saveReply(answer) {
    saveAnswers({ [questionNumber]: answer });
    console.log(answers);
  }

  if (error) return <h1>ðŸ˜… Something went wrong ðŸ˜…</h1>;

  return (
    <section>
      <h1>Survey ðŸ§©</h1>
      {!isDataLoading && (
        <div>
          <StyledLink
            to={questionNumber > 1 && `/survey/${prevQuestionNumber}`}
            isVisible={questionNumber > 1 && questionNumber !== "results"}
          >
            Previous
          </StyledLink>
          <StyledLink
            to={
              surveyData[questionNumberInt + 1]
                ? `/survey/${nextQuestionNumber}`
                : "/survey/results"
            }
            isVisible={questionNumber !== "results"}
          >
            {surveyData[questionNumberInt + 1] ? "Next" : "Results"}
          </StyledLink>
        </div>
      )}

      <QWrapper>
        <h2>{questionNumberInt ? `Question ${questionNumber}` : "Results"}</h2>
        {isDataLoading ? <Loader /> : <p>{surveyData[questionNumberInt]}</p>}
      </QWrapper>
      <ReplyWrapper>
        <ReplyBox
          onClick={() => saveReply(true)}
          isSelected={answers[questionNumber] === true}
        >
          Oui
        </ReplyBox>
        <ReplyBox
          onClick={() => saveReply(false)}
          isSelected={answers[questionNumber] === false}
        >
          Non
        </ReplyBox>
      </ReplyWrapper>
    </section>
  );
};

export default Survey;
